<template>
  <div id="app">
    <img src="./assets/logo.png">
    <div class="post-container">
      <div
        v-for="post in posts"
        :key="post.id"
        class="post-item">
        {{post.title}}
      </div>
    </div>
  </div>
</template>

<script>
import loder from 'loder'

export default {
  name: 'app',
  data () {
    return {
      posts: []
    }
  },
  created () {
    // 使用Loader已经准备好的首屏数据
    const { data } = loder.get('data')
    this.posts = data
  }
}
</script>

